import React, { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Bed, Users, Activity, Heart, Shield } from 'lucide-react';

interface BedData {
  id: string;
  number: string;
  status: 'available' | 'occupied' | 'reserved';
  ward: string;
  type: 'icu' | 'general' | 'isolation';
  floor: number;
  patientName?: string;
}

interface HospitalBedLayoutProps {
  hospitalId: number;
  onBedUpdate?: (bedId: string, status: string) => void;
}

export function HospitalBedLayout({ hospitalId, onBedUpdate }: HospitalBedLayoutProps) {
  const [beds, setBeds] = useState<BedData[]>([]);
  const [selectedWard, setSelectedWard] = useState<string>('all');
  const [selectedType, setSelectedType] = useState<'all' | 'icu' | 'general' | 'isolation'>('all');

  // Fetch real bed status data from server
  const { data: bedStatusData, isLoading: bedStatusLoading } = useQuery({
    queryKey: [`/api/hospitals/${hospitalId}/bed-status`],
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  // Get hospital-specific configurations
  const getHospitalConfig = (hospitalId: number) => {
    const configs = {
      2: { // Apollo Hospital Indore
        name: 'Apollo Hospital Indore',
        icuBeds: 30,
        generalBeds: 60, // Display subset for UI
        isolationBeds: 25,
        wards: ['Cardiology ICU', 'Orthopedic Ward', 'Neurology ICU', 'Emergency Ward', 'COVID Isolation']
      },
      3: { // CARE CHL Hospital Indore  
        name: 'CARE CHL Hospital Indore',
        icuBeds: 35,
        generalBeds: 65,
        isolationBeds: 30,
        wards: ['Cardiac ICU', 'Neurology Ward', 'Critical Care ICU', 'General Ward', 'Isolation Unit']
      },
      4: { // Bombay Hospital Indore
        name: 'Bombay Hospital Indore',
        icuBeds: 30,
        generalBeds: 55,
        isolationBeds: 25,
        wards: ['Multi-specialty ICU', 'Pediatric Ward', 'Surgery ICU', 'General Ward', 'Isolation Ward']
      }
    };
    return configs[hospitalId as keyof typeof configs] || configs[2];
  };

  // Generate bed data by merging server data with static configurations
  useEffect(() => {
    const config = getHospitalConfig(hospitalId);

    const generateBedsFromServerData = (): BedData[] => {
      if (!bedStatusData || bedStatusLoading) {
        return [];
      }

      // Static patient name mappings for occupied beds
      const patientNames: Record<string, string> = {
        // Apollo Hospital
        'CCU-01': 'Rajesh Kumar',
        'CCU-03': 'Priya Sharma', 
        'NICU-01': 'Baby Anaya',
        'MED-101': 'Amit Patel',
        'MED-103': 'Sunita Verma',
        'SUR-201': 'Vikram Singh',
        'SUR-203': 'Meera Gupta',

        // CARE CHL Hospital
        // 'NICU-01': 'Baby Krishna', // Duplicate key removed
        'NICU-03': 'Baby Lakshmi',
        // 'CCU-01': 'Ramesh Jain', // Duplicate key removed
        'PED-401': 'Little Arjun',
        'PED-403': 'Little Shreya',
        'CAR-502': 'Sushma Devi',
        'GEN-601': 'Dinesh Kumar',

        // Bombay Hospital
        'CCU-B1': 'Deepak Agarwal',
        'CCU-B3': 'Ritu Malhotra',
        'ICU-B1': 'Mohit Sharma',
        'ORT-501': 'Manoj Tiwari',
        'NEU-601': 'Sanjay Khanna',
        'GEN-702': 'Kavitha Reddy'
      };

      // Ward mappings for each hospital
      const wardMappings: Record<number, Record<string, { ward: string; floor: number }>> = {
        2: { // Apollo Hospital
          'CCU': { ward: 'Cardiac ICU', floor: 3 },
          'NICU': { ward: 'Neuro ICU', floor: 4 },
          'MED': { ward: 'Medical Ward A', floor: 1 },
          'SUR': { ward: 'Surgical Ward', floor: 2 },
          'ORT': { ward: 'Orthopedic Ward', floor: 2 }
        },
        3: { // CARE CHL Hospital
          'NICU': { ward: 'NICU', floor: 4 },
          'ICU': { ward: 'Critical Care ICU', floor: 3 },
          'CCU': { ward: 'Cardiac Care Unit', floor: 3 },
          'PED': { ward: 'Pediatric Ward', floor: 2 },
          'CAR': { ward: 'Cardiology Ward', floor: 1 },
          'GEN': { ward: 'General Medicine', floor: 4 },
          'MAT': { ward: 'Maternity Ward', floor: 5 }
        },
        4: { // Bombay Hospital
          'CCU': { ward: 'Coronary Care Unit', floor: 3 },
          'NICU': { ward: 'Neuro ICU', floor: 4 },
          'ICU': { ward: 'Multi-specialty ICU', floor: 3 },
          'ORT': { ward: 'Orthopedic Ward', floor: 2 },
          'NEU': { ward: 'Neurology Ward', floor: 4 },
          'GEN': { ward: 'General Medicine', floor: 1 },
          'SUR': { ward: 'Surgery Ward', floor: 3 },
          'PED': { ward: 'Pediatric Ward', floor: 2 }
        }
      };

      if (!Array.isArray(bedStatusData)) {
        return [];
      }
      return bedStatusData.map((bedStatus: any, index: number) => {
        const wardPrefix = bedStatus.bedNumber.split('-')[0];
        const wardInfo = wardMappings[hospitalId]?.[wardPrefix] || { ward: 'General Ward', floor: 1 };

        return {
          id: `bed-${bedStatus.id}`,
          number: bedStatus.bedNumber,
          status: bedStatus.status as 'available' | 'occupied' | 'reserved',
          ward: wardInfo.ward,
          type: bedStatus.bedType as 'icu' | 'general' | 'isolation',
          floor: wardInfo.floor,
          patientName: bedStatus.status === 'occupied' ? patientNames[bedStatus.bedNumber] : undefined
        };
      });
    };

    setBeds(generateBedsFromServerData());
  }, [hospitalId, bedStatusData, bedStatusLoading]);

  const updateBedStatus = (bedId: string, newStatus: 'available' | 'occupied' | 'reserved') => {
    setBeds(prev => {
      const updatedBeds = prev.map(bed => 
        bed.id === bedId 
          ? { ...bed, status: newStatus, patientName: newStatus === 'occupied' ? bed.patientName || `Patient ${bedId}` : undefined }
          : bed
      );

      // Calculate and update hospital bed counts in database
      const icuBeds = updatedBeds.filter(bed => bed.type === 'icu');
      const generalBeds = updatedBeds.filter(bed => bed.type === 'general');

      const availableIcuBeds = icuBeds.filter(bed => bed.status === 'available').length;
      const availableGeneralBeds = generalBeds.filter(bed => bed.status === 'available').length;

      // Update hospital bed counts in the database
      fetch(`/api/hospitals/${hospitalId}/beds`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          totalBeds: generalBeds.length,
          availableBeds: availableGeneralBeds,
          icuBeds: icuBeds.length,
          availableIcuBeds: availableIcuBeds
        })
      });

      return updatedBeds;
    });

    onBedUpdate?.(bedId, newStatus);
    console.log(`Bed ${bedId} updated to ${newStatus}`);
  };

  const getBedIcon = (type: string) => {
    switch (type) {
      case 'icu': return <Heart className="h-4 w-4" />;
      case 'isolation': return <Shield className="h-4 w-4" />;
      default: return <Bed className="h-4 w-4" />;
    }
  };

  const getBedColor = (status: string) => {
    switch (status) {
      case 'available': return 'bg-green-500 hover:bg-green-600 border-green-600';
      case 'occupied': return 'bg-red-500 hover:bg-red-600 border-red-600';
      case 'reserved': return 'bg-blue-500 hover:bg-blue-600 border-blue-600';
      default: return 'bg-gray-400';
    }
  };

  const filteredBeds = beds.filter(bed => {
    if (selectedType !== 'all' && bed.type !== selectedType) return false;
    if (selectedWard !== 'all' && bed.ward !== selectedWard) return false;
    return true;
  });

  const groupedBeds = filteredBeds.reduce((acc, bed) => {
    const key = selectedType === 'all' ? bed.type : bed.ward;
    if (!acc[key]) acc[key] = [];
    acc[key].push(bed);
    return acc;
  }, {} as Record<string, BedData[]>);

  const getStats = () => {
    const total = beds.length;
    const available = beds.filter(b => b.status === 'available').length;
    const occupied = beds.filter(b => b.status === 'occupied').length;
    const reserved = beds.filter(b => b.status === 'reserved').length;

    return { total, available, occupied, reserved };
  };

  const stats = getStats();
  const uniqueWards = Array.from(new Set(beds.map(b => b.ward)));

  const config = getHospitalConfig(hospitalId);

  return (
    <div className="space-y-6">
      {/* Hospital Header */}
      <div>
        <h2 className="text-2xl font-bold text-gray-800">{config.name}</h2>
        <p className="text-gray-600 mb-4">Bed capacity: {config.icuBeds + config.generalBeds + config.isolationBeds} beds total</p>
      </div>

      {/* Bed Statistics Header */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="pt-4">
            <div className="flex items-center space-x-2">
              <Bed className="h-5 w-5 text-blue-600" />
              <div>
                <p className="text-sm font-medium text-gray-600">Total Beds</p>
                <p className="text-2xl font-bold">{stats.total}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-4">
            <div className="flex items-center space-x-2">
              <div className="w-3 h-3 bg-green-500 rounded-full"></div>
              <div>
                <p className="text-sm font-medium text-gray-600">Available</p>
                <p className="text-2xl font-bold text-green-600">{stats.available}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-4">
            <div className="flex items-center space-x-2">
              <div className="w-3 h-3 bg-red-500 rounded-full"></div>
              <div>
                <p className="text-sm font-medium text-gray-600">Occupied</p>
                <p className="text-2xl font-bold text-red-600">{stats.occupied}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-4">
            <div className="flex items-center space-x-2">
              <div className="w-3 h-3 bg-blue-500 rounded-full"></div>
              <div>
                <p className="text-sm font-medium text-gray-600">Reserved</p>
                <p className="text-2xl font-bold text-blue-600">{stats.reserved}</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <Card>
        <CardHeader>
          <CardTitle>Bed Layout Management</CardTitle>
        </CardHeader>
        <CardContent>
          <Tabs value={selectedType} onValueChange={(value: any) => setSelectedType(value)}>
            <TabsList className="grid grid-cols-4 w-full max-w-md">
              <TabsTrigger value="all">All Beds</TabsTrigger>
              <TabsTrigger value="icu">ICU</TabsTrigger>
              <TabsTrigger value="general">General</TabsTrigger>
              <TabsTrigger value="isolation">Isolation</TabsTrigger>
            </TabsList>

            <div className="mt-4 space-y-6">
              {Object.entries(groupedBeds).map(([groupName, groupBeds]) => (
                <div key={groupName} className="space-y-3">
                  <div className="flex items-center space-x-2">
                    {getBedIcon(groupBeds[0]?.type)}
                    <h3 className="text-lg font-semibold capitalize">
                      {selectedType === 'all' ? `${groupName.toUpperCase()} Beds` : groupName}
                    </h3>
                    <Badge variant="outline">
                      {groupBeds.length} beds
                    </Badge>
                  </div>

                  <div className="grid grid-cols-5 sm:grid-cols-8 md:grid-cols-10 lg:grid-cols-12 gap-2">
                    {groupBeds.map(bed => (
                      <Popover key={bed.id}>
                        <PopoverTrigger asChild>
                          <div
                            className={`
                              relative p-2 rounded-lg border-2 cursor-pointer transition-all duration-200
                              ${getBedColor(bed.status)}
                              hover:scale-105 hover:shadow-lg
                            `}
                          >
                            <div className="text-center">
                              <div className="text-white text-xs font-medium">
                                {bed.number}
                              </div>
                              {bed.patientName && (
                                <div className="text-white text-xs opacity-80 truncate">
                                  {bed.patientName}
                                </div>
                              )}
                            </div>

                            {/* Status indicator */}
                            <div className="absolute -top-1 -right-1 w-3 h-3 rounded-full bg-white border border-gray-300">
                              <div className={`w-full h-full rounded-full ${
                                bed.status === 'available' ? 'bg-green-400' :
                                bed.status === 'occupied' ? 'bg-red-400' : 'bg-blue-400'
                              }`}></div>
                            </div>
                          </div>
                        </PopoverTrigger>

                        <PopoverContent className="w-56" side="top">
                          <div className="space-y-3">
                            <div>
                              <h4 className="font-semibold">Bed {bed.number}</h4>
                              <p className="text-sm text-gray-600">{bed.ward} ‚Ä¢ Floor {bed.floor}</p>
                              {bed.patientName && (
                                <p className="text-sm text-blue-600">Patient: {bed.patientName}</p>
                              )}
                            </div>

                            <div className="flex flex-col space-y-2">
                              <Button
                                size="sm"
                                variant={bed.status === 'available' ? 'default' : 'outline'}
                                onClick={() => updateBedStatus(bed.id, 'available')}
                                className="text-xs"
                              >
                                üü¢ Set Available
                              </Button>
                              <Button
                                size="sm"
                                variant={bed.status === 'occupied' ? 'default' : 'outline'}
                                onClick={() => updateBedStatus(bed.id, 'occupied')}
                                className="text-xs"
                              >
                                üî¥ Set Occupied
                              </Button>
                              <Button
                                size="sm"
                                variant={bed.status === 'reserved' ? 'default' : 'outline'}
                                onClick={() => updateBedStatus(bed.id, 'reserved')}
                                className="text-xs"
                              >
                                üîµ Set Reserved
                              </Button>
                            </div>
                          </div>
                        </PopoverContent>
                      </Popover>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </Tabs>
        </CardContent>
      </Card>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Bed, Users, Activity, Heart, Shield, MapPin, Settings, RotateCcw } from 'lucide-react';
import { getHospitalPatientNames } from '@/data/patient-names';

interface BedData {
  id: string;
  number: string;
  status: 'available' | 'occupied' | 'reserved';
  ward: string;
  type: 'icu' | 'general' | 'isolation';
  floor: number;
  patientName?: string;
}

interface HospitalBedLayoutProps {
  hospitalId: number;
  onBedUpdate?: (bedId: string, status: string) => void;
}

export function HospitalBedLayout({ hospitalId, onBedUpdate }: HospitalBedLayoutProps) {
  const [beds, setBeds] = useState<BedData[]>([]);
  const [selectedWard, setSelectedWard] = useState<string>('all');
  const [selectedType, setSelectedType] = useState<'all' | 'icu' | 'general' | 'isolation'>('all');

  // OPTIMIZED HOSPITAL ACCESS: No mapping needed anymore
  // The backend now supports both hospital ID and user ID for all operations
  // This eliminates the need for frontend mapping and enables independent hospital operations

  // Direct access using either hospital ID or user ID (backend handles both)
  const { data: bedStatusData, isLoading: bedStatusLoading } = useQuery({
    queryKey: [`/api/hospitals/${hospitalId}/bed-status`],
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  // Get hospital-specific configurations
  const getHospitalConfig = (hospitalId: number) => {
    const configs = {
      2: { // Apollo Hospital Indore
        name: 'Apollo Hospital Indore',
        icuBeds: 30,
        generalBeds: 60,
        isolationBeds: 25,
        wards: ['Cardiology ICU', 'Orthopedic Ward', 'Neurology ICU', 'Emergency Ward', 'COVID Isolation']
      },
      3: { // CARE CHL Hospital Indore  
        name: 'CARE CHL Hospital Indore',
        icuBeds: 35,
        generalBeds: 65,
        isolationBeds: 30,
        wards: ['Cardiac ICU', 'Neurology Ward', 'Critical Care ICU', 'General Ward', 'Isolation Unit']
      },
      4: { // Bombay Hospital Indore
        name: 'Bombay Hospital Indore',
        icuBeds: 30,
        generalBeds: 55,
        isolationBeds: 25,
        wards: ['Multi-specialty ICU', 'Pediatric Ward', 'Surgery ICU', 'General Ward', 'Isolation Ward']
      }
    };
    return configs[hospitalId as keyof typeof configs] || configs[2];
  };

  // Generate bed data from server with enhanced patient info
  useEffect(() => {
    if (!bedStatusData || bedStatusLoading) {
      return;
    }

    // Authentic Indian patient names from expanded database
    const patientNames = getHospitalPatientNames(hospitalId);

    // Ward mappings for each hospital
    const wardMappings: Record<number, Record<string, { ward: string; floor: number }>> = {
      2: { // Apollo Hospital
        'CCU': { ward: 'Cardiac ICU', floor: 3 },
        'NICU': { ward: 'Neuro ICU', floor: 4 },
        'MED': { ward: 'Medical Ward A', floor: 1 },
        'SUR': { ward: 'Surgical Ward', floor: 2 },
        'ORT': { ward: 'Orthopedic Ward', floor: 2 }
      },
      3: { // CARE CHL Hospital
        'NICU': { ward: 'NICU', floor: 4 },
        'ICU': { ward: 'Critical Care ICU', floor: 3 },
        'CCU': { ward: 'Cardiac Care Unit', floor: 3 },
        'PED': { ward: 'Pediatric Ward', floor: 2 },
        'CAR': { ward: 'Cardiology Ward', floor: 1 },
        'GEN': { ward: 'General Medicine', floor: 4 },
        'MAT': { ward: 'Maternity Ward', floor: 5 }
      },
      4: { // Bombay Hospital
        'CCU': { ward: 'Coronary Care Unit', floor: 3 },
        'NICU': { ward: 'Neuro ICU', floor: 4 },
        'ICU': { ward: 'Multi-specialty ICU', floor: 3 },
        'ORT': { ward: 'Orthopedic Ward', floor: 2 },
        'NEU': { ward: 'Neurology Ward', floor: 4 },
        'GEN': { ward: 'General Medicine', floor: 1 },
        'SUR': { ward: 'Surgery Ward', floor: 3 },
        'PED': { ward: 'Pediatric Ward', floor: 2 }
      }
    };

    // Function to get proper ward description from bed number
    const getWardDescription = (bedNumber: string, bedType: string): string => {
      // ICU bed types
      if (bedNumber.startsWith('CICU-')) return 'Cardiac ICU';
      if (bedNumber.startsWith('NICU-')) return 'Neonatal ICU';
      if (bedNumber.startsWith('SICU-')) return 'Surgical ICU';
      if (bedNumber.startsWith('PICU-')) return 'Pediatric ICU';
      if (bedNumber.startsWith('MICU-')) return 'Medical ICU';
      if (bedNumber.startsWith('TICU-')) return 'Trauma ICU';
      if (bedNumber.startsWith('RICU-')) return 'Respiratory ICU';
      if (bedNumber.startsWith('CVICU-')) return 'Cardiovascular ICU';
      if (bedNumber.startsWith('CCU-')) return 'Cardiac Care Unit';
      if (bedNumber.startsWith('HDU-')) return 'High Dependency Unit';

      // Specialty wards
      if (bedNumber.startsWith('MAT-')) return 'Maternity Ward';
      if (bedNumber.startsWith('PED-')) return 'Pediatric Ward';
      if (bedNumber.startsWith('SUR-')) return 'Surgery Ward';
      if (bedNumber.startsWith('MED-')) return 'Medicine Ward';
      if (bedNumber.startsWith('ORT-') || bedNumber.startsWith('ORTH-')) return 'Orthopedic Ward';
      if (bedNumber.startsWith('NEU-')) return 'Neurology Ward';
      if (bedNumber.startsWith('ONC-')) return 'Oncology Ward';
      if (bedNumber.startsWith('CAR-')) return 'Cardiology Ward';
      if (bedNumber.startsWith('GAS-')) return 'Gastroenterology Ward';
      if (bedNumber.startsWith('URO-')) return 'Urology Ward';
      if (bedNumber.startsWith('ENT-')) return 'ENT Ward';
      if (bedNumber.startsWith('OBS-')) return 'Obstetrics Ward';
      if (bedNumber.startsWith('GYN-')) return 'Gynecology Ward';

      // Isolation beds (if any)
      if (bedType === 'isolation') return 'Isolation Ward';

      // General beds
      if (bedNumber.startsWith('GEN-')) return 'General Ward';

      // Fallback based on bed type
      return bedType === 'icu' ? 'ICU' : 'General Ward';
    };

    const hospitalBeds = bedStatusData.map((bedStatus: any) => {
      const wardDescription = getWardDescription(bedStatus.bedNumber, bedStatus.bedType);
      const wardPrefix = bedStatus.bedNumber.split('-')[0];
      const wardInfo = wardMappings[hospitalId]?.[wardPrefix] || { ward: wardDescription, floor: 1 };

      // Debug specific bed
      // if (bedStatus.bedNumber === 'PED-08') {
      //   console.log('Debug PED-08 bed data:', bedStatus);
      // }

      return {
        id: `bed-${bedStatus.id}`,
        number: bedStatus.bedNumber,
        status: bedStatus.status as 'available' | 'occupied' | 'reserved',
        ward: wardDescription, // Use the proper ward description
        type: bedStatus.bedType as 'icu' | 'general' | 'isolation',
        floor: wardInfo.floor,
        patientName: bedStatus.patientName // Use ONLY real patient names from database
      };
    });

    setBeds(hospitalBeds);
  }, [hospitalId, bedStatusData, bedStatusLoading]);

  const updateBedStatus = (bedId: string, newStatus: 'available' | 'occupied' | 'reserved') => {
    setBeds(prev => {
      const updatedBeds = prev.map(bed => 
        bed.id === bedId 
          ? { ...bed, status: newStatus, patientName: newStatus === 'occupied' ? bed.patientName || `Patient ${bedId}` : undefined }
          : bed
      );

      // Calculate and update hospital bed counts in database
      const icuBeds = updatedBeds.filter(bed => bed.type === 'icu');
      const generalBeds = updatedBeds.filter(bed => bed.type === 'general');

      const availableIcuBeds = icuBeds.filter(bed => bed.status === 'available').length;
      const availableGeneralBeds = generalBeds.filter(bed => bed.status === 'available').length;

      // Update hospital bed counts in the database
      fetch(`/api/hospitals/${hospitalId}/beds`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          totalBeds: generalBeds.length,
          availableBeds: availableGeneralBeds,
          icuBeds: icuBeds.length,
          availableIcuBeds: availableIcuBeds
        })
      });

      return updatedBeds;
    });

    onBedUpdate?.(bedId, newStatus);
  };

  const getBedIcon = (type: string) => {
    switch (type) {
      case 'icu': return <Heart className="h-4 w-4" />;
      case 'isolation': return <Shield className="h-4 w-4" />;
      default: return <Bed className="h-4 w-4" />;
    }
  };

  const getBedColor = (status: string) => {
    switch (status) {
      case 'available': return 'bg-green-100 border-green-300 hover:bg-green-200';
      case 'occupied': return 'bg-red-100 border-red-300 hover:bg-red-200';
      case 'reserved': return 'bg-yellow-100 border-yellow-300 hover:bg-yellow-200';
      default: return 'bg-gray-100 border-gray-300';
    }
  };

  const getBadgeColor = (status: string) => {
    switch (status) {
      case 'available': return 'bg-green-500';
      case 'occupied': return 'bg-red-500';
      case 'reserved': return 'bg-yellow-500';
      default: return 'bg-gray-500';
    }
  };

  // Filter beds based on selected ward and type
  const filteredBeds = beds.filter(bed => {
    const wardMatch = selectedWard === 'all' || bed.ward === selectedWard;
    const typeMatch = selectedType === 'all' || bed.type === selectedType;
    return wardMatch && typeMatch;
  });

  // Get unique wards for filtering
  const uniqueWards = [...new Set(beds.map(bed => bed.ward))];

  // Calculate bed statistics
  const stats = {
    total: beds.length,
    available: beds.filter(bed => bed.status === 'available').length,
    occupied: beds.filter(bed => bed.status === 'occupied').length,
    reserved: beds.filter(bed => bed.status === 'reserved').length,
    icu: beds.filter(bed => bed.type === 'icu').length,
    general: beds.filter(bed => bed.type === 'general').length
  };

  if (bedStatusLoading) {
    return (
      <Card>
        <CardContent className="p-6">
          <div className="text-center">Loading bed status...</div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* Hospital Bed Statistics */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Users className="h-5 w-5 text-blue-600" />
              <div>
                <p className="text-sm text-gray-600">Total Beds</p>
                <p className="text-2xl font-bold">{stats.total}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Bed className="h-5 w-5 text-green-600" />
              <div>
                <p className="text-sm text-gray-600">Available</p>
                <p className="text-2xl font-bold text-green-600">{stats.available}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Activity className="h-5 w-5 text-red-600" />
              <div>
                <p className="text-sm text-gray-600">Occupied</p>
                <p className="text-2xl font-bold text-red-600">{stats.occupied}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Heart className="h-5 w-5 text-purple-600" />
              <div>
                <p className="text-sm text-gray-600">ICU Beds</p>
                <p className="text-2xl font-bold text-purple-600">{stats.icu}</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <Card>
        <CardHeader>
          <CardTitle>Bed Layout - Real-time Status</CardTitle>
        </CardHeader>
        <CardContent>
          {/* Enhanced Filter Section */}
          <div className="flex justify-center mb-8">
            <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-2xl p-6 shadow-sm border border-blue-100">
              <div className="flex flex-col md:flex-row items-center gap-6">
                {/* Ward Filter */}
                <div className="flex flex-col items-center">
                  <label className="text-sm font-semibold text-gray-700 mb-3 flex items-center gap-2">
                    <MapPin className="h-4 w-4 text-blue-600" />
                    Filter by Ward
                  </label>
                  <select 
                    value={selectedWard} 
                    onChange={(e) => setSelectedWard(e.target.value)}
                    className="min-w-[200px] border-2 border-blue-200 rounded-xl px-4 py-3 bg-white shadow-sm hover:border-blue-300 focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all duration-200 text-gray-700 font-medium"
                  >
                    <option value="all">üè• All Wards</option>
                    {uniqueWards.map(ward => (
                      <option key={ward} value={ward}>
                        {ward === 'Cardiac ICU' ? '‚ù§Ô∏è' : 
                         ward === 'Neonatal ICU' ? 'üë∂' :
                         ward === 'Surgical ICU' ? 'üè•' :
                         ward === 'Maternity Ward' ? 'ü§±' :
                         ward === 'General Ward' ? 'üõèÔ∏è' : 'üîπ'} {ward}
                      </option>
                    ))}
                  </select>
                </div>

                {/* Divider */}
                <div className="hidden md:block h-16 w-px bg-gradient-to-b from-blue-200 to-transparent"></div>

                {/* Type Filter */}
                <div className="flex flex-col items-center">
                  <label className="text-sm font-semibold text-gray-700 mb-3 flex items-center gap-2">
                    <Settings className="h-4 w-4 text-indigo-600" />
                    Filter by Type
                  </label>
                  <select 
                    value={selectedType} 
                    onChange={(e) => setSelectedType(e.target.value as any)}
                    className="min-w-[200px] border-2 border-indigo-200 rounded-xl px-4 py-3 bg-white shadow-sm hover:border-indigo-300 focus:border-indigo-500 focus:ring-2 focus:ring-indigo-200 transition-all duration-200 text-gray-700 font-medium"
                  >
                    <option value="all">üè® All Types</option>
                    <option value="icu">üö® ICU Beds</option>
                    <option value="general">üõèÔ∏è General Beds</option>
                    <option value="isolation">üîí Isolation Beds</option>
                  </select>
                </div>

                {/* Reset Filters Button */}
                <div className="flex flex-col items-center">
                  <label className="text-sm font-semibold text-gray-700 mb-3 flex items-center gap-2">
                    <RotateCcw className="h-4 w-4 text-gray-600" />
                    Quick Actions
                  </label>
                  <button
                    onClick={() => {
                      setSelectedWard('all');
                      setSelectedType('all');
                    }}
                    className="min-w-[120px] bg-gradient-to-r from-gray-100 to-gray-200 hover:from-gray-200 hover:to-gray-300 text-gray-700 font-medium py-3 px-4 rounded-xl border-2 border-gray-200 hover:border-gray-300 transition-all duration-200 shadow-sm"
                  >
                    Reset All
                  </button>
                </div>
              </div>
            </div>
          </div>

          {/* Bed Grid */}
          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
            {filteredBeds.map((bed) => (
              <Popover key={bed.id}>
                <PopoverTrigger asChild>
                  <div className={`relative p-4 rounded-lg border-2 cursor-pointer transition-all ${getBedColor(bed.status)}`}>
                    <div className="flex items-center justify-between mb-2">
                      {getBedIcon(bed.type)}
                      <Badge className={`text-white text-xs ${getBadgeColor(bed.status)}`}>
                        {bed.status}
                      </Badge>
                    </div>
                    <div className="text-sm font-medium">{bed.number}</div>
                    <div className="text-xs text-gray-600">{bed.ward}</div>
                    {bed.patientName && (
                      <div className="text-xs font-medium mt-1 text-blue-600">{bed.patientName}</div>
                    )}
                    <div className="text-xs text-gray-500">Floor {bed.floor}</div>
                  </div>
                </PopoverTrigger>
                <PopoverContent className="w-80">
                  <div className="space-y-4">
                    <div>
                      <h3 className="font-semibold">{bed.number}</h3>
                      <p className="text-sm text-gray-600">{bed.ward} - Floor {bed.floor}</p>
                      <p className="text-sm">Type: {bed.type.toUpperCase()}</p>
                      {bed.patientName && (
                        <p className="text-sm"><strong>Patient:</strong> {bed.patientName}</p>
                      )}
                    </div>
                    <div className="space-y-2">
                      <p className="text-sm font-medium">Update Status:</p>
                      <div className="flex gap-2">
                        <Button 
                          size="sm" 
                          variant={bed.status === 'available' ? 'default' : 'outline'}
                          onClick={() => updateBedStatus(bed.id, 'available')}
                        >
                          Available
                        </Button>
                        <Button 
                          size="sm" 
                          variant={bed.status === 'occupied' ? 'default' : 'outline'}
                          onClick={() => updateBedStatus(bed.id, 'occupied')}
                        >
                          Occupied
                        </Button>
                        <Button 
                          size="sm" 
                          variant={bed.status === 'reserved' ? 'default' : 'outline'}
                          onClick={() => updateBedStatus(bed.id, 'reserved')}
                        >
                          Reserved
                        </Button>
                      </div>
                    </div>
                  </div>
                </PopoverContent>
              </Popover>
            ))}
          </div>

          {filteredBeds.length === 0 && (
            <div className="text-center py-8 text-gray-500">
              No beds found for the selected filters.
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/hospital-communica.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { useWebSocket } from "@/hooks/use-websocket";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { MessageSquare, Send, User, Clock } from "lucide-react";

interface HospitalCommunicationProps {
  emergencyRequestId?: number;
}

export function HospitalCommunication({ emergencyRequestId }: HospitalCommunicationProps) {
  const [message, setMessage] = useState("");
  const { sendMessage: sendWebSocketMessage } = useWebSocket();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: communications, isLoading } = useQuery({
    queryKey: ['/api/communications', emergencyRequestId],
    enabled: !!emergencyRequestId,
    refetchInterval: 2000,
  });

  const sendMessageMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/communications", data);
      return response.json();
    },
    onSuccess: () => {
      setMessage("");
      queryClient.invalidateQueries({ queryKey: ['/api/communications', emergencyRequestId] });
    },
    onError: () => {
      toast({
        title: "Message Failed",
        description: "Failed to send message. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = () => {
    if (!message.trim() || !emergencyRequestId) return;

    // Send via WebSocket for real-time delivery
    sendWebSocketMessage({
      type: 'chat_message',
      emergencyRequestId,
      message: message.trim(),
    });

    // Also save to database
    sendMessageMutation.mutate({
      emergencyRequestId,
      message: message.trim(),
      messageType: 'text',
    });
  };

  if (!emergencyRequestId) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>Communication Center</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center py-8 text-gray-500">
            <MessageSquare className="w-12 h-12 mx-auto mb-4 text-gray-400" />
            <p>Select an emergency request to start communication</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle>Emergency Communication</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="bg-gray-50 rounded-lg p-4 h-64 overflow-y-auto mb-4">
          {isLoading ? (
            <div className="space-y-3">
              {[1, 2, 3].map((i) => (
                <div key={i} className="flex items-start animate-pulse">
                  <div className="w-8 h-8 bg-gray-300 rounded-full mr-3"></div>
                  <div className="flex-1">
                    <div className="w-32 h-4 bg-gray-300 rounded mb-2"></div>
                    <div className="w-full h-3 bg-gray-300 rounded"></div>
                  </div>
                </div>
              ))}
            </div>
          ) : communications && communications.length > 0 ? (
            <div className="space-y-3">
              {communications.map((comm: any) => (
                <div key={comm.id} className="flex items-start">
                  <div className="bg-blue-600 text-white rounded-full w-8 h-8 flex items-center justify-center text-sm mr-3">
                    <User className="w-4 h-4" />
                  </div>
                  <div className="flex-1">
                    <div className="bg-white p-3 rounded-lg shadow-sm">
                      <div className="text-sm font-medium text-gray-800 mb-1">
                        {comm.senderId === 'hospital' ? 'Hospital Staff' : 'Ambulance Team'}
                      </div>
                      <div className="text-sm text-gray-600">{comm.message}</div>
                    </div>
                    <div className="text-xs text-gray-500 mt-1 flex items-center">
                      <Clock className="w-3 h-3 mr-1" />
                      {new Date(comm.createdAt).toLocaleTimeString()}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8 text-gray-500">
              <MessageSquare className="w-8 h-8 mx-auto mb-2 text-gray-400" />
              <p>No messages yet</p>
              <p className="text-sm">Start the conversation</p>
            </div>
          )}
        </div>

        <div className="flex space-x-2">
          <Input
            placeholder="Type your message..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
            disabled={sendMessageMutation.isPending}
          />
          <Button 
            onClick={handleSendMessage}
            disabled={!message.trim() || sendMessageMutation.isPending}
            className="bg-blue-600 hover:bg-blue-700"
          >
            <Send className="w-4 h-4" />
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/navigation-map.tsx.content:
import React, { useEffect, useRef, useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Navigation as NavigationIcon, Clock, MapPin, Play, ExternalLink, RotateCcw, Smartphone } from 'lucide-react';

interface NavigationMapProps {
  ambulanceLocation: { latitude: number; longitude: number };
  patientLocation: { latitude: number; longitude: number };
  onStartJourney: () => void;
  onJourneyUpdate?: (eta: number, distance: number) => void;
  isJourneyActive: boolean;
}

export function NavigationMap({ 
  ambulanceLocation, 
  patientLocation, 
  onStartJourney,
  onJourneyUpdate,
  isJourneyActive 
}: NavigationMapProps) {

  // Removed excessive debug logging to prevent map reloading
  const mapRef = useRef<HTMLDivElement>(null);
  const mapInstanceRef = useRef<google.maps.Map | null>(null);
  const directionsServiceRef = useRef<google.maps.DirectionsService | null>(null);
  const directionsRendererRef = useRef<google.maps.DirectionsRenderer | null>(null);
  const [isMapLoaded, setIsMapLoaded] = useState(false);
  const [routeInfo, setRouteInfo] = useState<{
    distance: string;
    duration: string;
    eta: number;
  } | null>(null);
  const [isCalculating, setIsCalculating] = useState(false);
  const [navigationUrl, setNavigationUrl] = useState<string>('');

  // Load Google Maps
  useEffect(() => {
    const loadGoogleMaps = async () => {
      if (window.google && window.google.maps) {
        setIsMapLoaded(true);
        return;
      }

      const existingScript = document.querySelector('script[src*="maps.googleapis.com"]');
      if (existingScript) {
        existingScript.addEventListener('load', () => setIsMapLoaded(true));
        return;
      }

      try {
        const response = await fetch('/api/maps/config');
        const { apiKey } = await response.json();

        const script = document.createElement('script');
        script.src = `https://maps.googleapis.com/maps/api/js?key=${apiKey}&libraries=geometry,places`;
        script.async = true;
        script.defer = true;
        script.onload = () => setIsMapLoaded(true);
        document.head.appendChild(script);
      } catch (error) {
        console.error('Failed to load Google Maps:', error);
      }
    };

    loadGoogleMaps();
  }, []);

  // Initialize map and directions
  useEffect(() => {
    if (!isMapLoaded || !mapRef.current || !ambulanceLocation || !patientLocation) return;

    const mapOptions: google.maps.MapOptions = {
      zoom: 13,
      mapTypeControl: true,
      streetViewControl: true,
      fullscreenControl: true,
      zoomControl: true,
      mapTypeId: google.maps.MapTypeId.ROADMAP,
      gestureHandling: 'greedy',
      disableDefaultUI: false,
      clickableIcons: true
    };

    const map = new google.maps.Map(mapRef.current, mapOptions);
    mapInstanceRef.current = map;

    // Initialize directions service and renderer with advanced options
    directionsServiceRef.current = new google.maps.DirectionsService();
    directionsRendererRef.current = new google.maps.DirectionsRenderer({
      suppressMarkers: false,
      draggable: false,
      preserveViewport: false,
      polylineOptions: {
        strokeColor: '#DC2626',
        strokeWeight: 6,
        strokeOpacity: 0.9,
        geodesic: true
      },
      markerOptions: {
        icon: {
          url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
            <svg width="32" height="32" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <circle cx="12" cy="12" r="10" fill="#DC2626" stroke="#FFFFFF" stroke-width="2"/>
              <path d="M12 8v4l3 3" stroke="#FFFFFF" stroke-width="2" stroke-linecap="round"/>
            </svg>
          `),
          scaledSize: new google.maps.Size(32, 32),
          anchor: new google.maps.Point(16, 16)
        }
      }
    });
    directionsRendererRef.current.setMap(map);

    calculateRoute();
    generateNavigationUrl();
  }, [isMapLoaded, ambulanceLocation, patientLocation]);

  const calculateRoute = async () => {
    if (!directionsServiceRef.current || !directionsRendererRef.current) return;

    setIsCalculating(true);

    const request: google.maps.DirectionsRequest = {
      origin: { lat: ambulanceLocation.latitude, lng: ambulanceLocation.longitude },
      destination: { lat: patientLocation.latitude, lng: patientLocation.longitude },
      travelMode: google.maps.TravelMode.DRIVING,
      optimizeWaypoints: true,
      avoidHighways: false,
      avoidTolls: false,
      drivingOptions: {
        departureTime: new Date(),
        trafficModel: google.maps.TrafficModel.BEST_GUESS
      },
      unitSystem: google.maps.UnitSystem.METRIC,
      region: 'IN' // India region for better routing
    };

    try {
      const result = await directionsServiceRef.current.route(request);
      directionsRendererRef.current.setDirections(result);

      const route = result.routes[0];
      const leg = route.legs[0];

      const routeData = {
        distance: leg.distance?.text || 'Unknown',
        duration: leg.duration?.text || 'Unknown',
        eta: leg.duration?.value || 0 // in seconds
      };

      setRouteInfo(routeData);

      // Notify parent component about route info
      if (onJourneyUpdate) {
        onJourneyUpdate(routeData.eta, leg.distance?.value || 0);
      }

    } catch (error) {
      console.error('Error calculating route:', error);
    } finally {
      setIsCalculating(false);
    }
  };

  const generateNavigationUrl = () => {
    // Generate Google Maps navigation URL for native app integration
    const origin = `${ambulanceLocation.latitude},${ambulanceLocation.longitude}`;
    const destination = `${patientLocation.latitude},${patientLocation.longitude}`;

    // Google Maps URL with navigation parameters
    const googleMapsUrl = `https://www.google.com/maps/dir/${origin}/${destination}/@${destination},15z/data=!3m1!4b1!4m2!4m1!3e0`;

    setNavigationUrl(googleMapsUrl);
  };

  const openNativeNavigation = () => {
    // Try to open in Google Maps app first, fallback to web
    const mobileNavigationUrl = `google.navigation:q=${patientLocation.latitude},${patientLocation.longitude}&mode=d`;

    // For mobile devices, try app first
    if (/Android|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)) {
      window.open(mobileNavigationUrl, '_blank');
      // Fallback to web version after a delay
      setTimeout(() => {
        window.open(navigationUrl, '_blank');
      }, 1500);
    } else {
      // Desktop - open web version
      window.open(navigationUrl, '_blank');
    }
  };

  const handleStartJourney = () => {
    onStartJourney();
  };

  const formatETA = (seconds: number): string => {
    const minutes = Math.ceil(seconds / 60);
    if (minutes < 60) {
      return `${minutes} min`;
    }
    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;
    return `${hours}h ${remainingMinutes}m`;
  };

  return (
    <Card className="w-full">
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <NavigationIcon className="w-5 h-5 text-blue-600" />
            <span>Emergency Navigation</span>
          </div>
          <div className="flex items-center space-x-2">
            {routeInfo && (
              <Badge variant="outline" className="text-sm">
                <Clock className="w-3 h-3 mr-1" />
                ETA: {formatETA(routeInfo.eta)}
              </Badge>
            )}
            {isJourneyActive && (
              <Badge className="bg-green-600">
                <Play className="w-3 h-3 mr-1" />
                En Route
              </Badge>
            )}
          </div>
        </CardTitle>
      </CardHeader>
      <CardContent>
        {/* Map Container */}
        <div 
          ref={mapRef}
          className="w-full h-96 rounded-lg border mb-4"
          style={{ minHeight: '400px' }}
        />

        {/* Route Information */}
        {routeInfo && (
          <div className="grid grid-cols-2 gap-4 mb-4">
            <div className="bg-blue-50 p-3 rounded-lg">
              <div className="flex items-center space-x-2">
                <MapPin className="w-4 h-4 text-blue-600" />
                <span className="text-sm font-medium">Distance</span>
              </div>
              <div className="text-xl font-bold text-blue-700">
                {routeInfo.distance}
              </div>
            </div>
            <div className="bg-green-50 p-3 rounded-lg">
              <div className="flex items-center space-x-2">
                <Clock className="w-4 h-4 text-green-600" />
                <span className="text-sm font-medium">Estimated Time</span>
              </div>
              <div className="text-xl font-bold text-green-700">
                {routeInfo.duration}
              </div>
            </div>
          </div>
        )}

        {/* Journey Controls */}
        <div className="space-y-3">
          <div className="flex space-x-2">
            {!isJourneyActive ? (
              <Button 
                onClick={handleStartJourney}
                className="flex-1 bg-green-600 hover:bg-green-700"
                disabled={isCalculating || !routeInfo}
              >
                <Play className="w-4 h-4 mr-2" />
                Start Journey
              </Button>
            ) : (
              <div className="flex-1 bg-green-100 border border-green-300 rounded-md p-2 text-center">
                <div className="flex items-center justify-center space-x-2 text-green-800">
                  <Play className="w-4 h-4" />
                  <span className="font-medium">Journey in Progress</span>
                </div>
              </div>
            )}

            <Button 
              variant="outline" 
              onClick={calculateRoute}
              disabled={isCalculating}
            >
              <RotateCcw className={`w-4 h-4 mr-2 ${isCalculating ? 'animate-spin' : ''}`} />
              Recalculate
            </Button>
          </div>

          {/* Native Navigation */}
          <div className="flex space-x-2">
            <Button 
              onClick={openNativeNavigation}
              variant="outline"
              className="flex-1 border-blue-200 hover:bg-blue-50"
              disabled={!navigationUrl}
            >
              <Smartphone className="w-4 h-4 mr-2" />
              Open in Google Maps
            </Button>

            <Button 
              onClick={() => window.open(navigationUrl, '_blank')}
              variant="outline"
              className="border-blue-200 hover:bg-blue-50"
              disabled={!navigationUrl}
            >
              <ExternalLink className="w-4 h-4" />
            </Button>
          </div>
        </div>

        {isCalculating && (
          <div className="mt-4 text-center text-gray-600">
            <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-600 mx-auto mb-2"></div>
            Calculating optimal route...
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/notification-syste.content:
import { useState, useEffect } from 'react';
import { useWebSocket } from '@/hooks/use-websocket';
import { useToast } from '@/hooks/use-toast';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Bell, X, AlertTriangle, CheckCircle, Clock, MessageSquare } from 'lucide-react';

interface Notification {
  id: string;
  type: 'emergency' | 'ambulance' | 'hospital' | 'communication';
  title: string;
  message: string;
  priority: 'low' | 'medium' | 'high' | 'critical';
  timestamp: Date;
  read: boolean;
  actionable?: boolean;
  data?: any;
}

interface NotificationSystemProps {
  userRole: string;
  userId: number;
}

export function NotificationSystem({ userRole, userId }: NotificationSystemProps) {
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [showNotifications, setShowNotifications] = useState(false);
  const { lastMessage } = useWebSocket();
  const { toast } = useToast();

  // Handle incoming WebSocket messages and convert to notifications
  useEffect(() => {
    if (lastMessage && lastMessage.event && lastMessage.data) {
      const { event, data } = lastMessage;
      let newNotification: Notification | null = null;

      try {
        switch (event) {
          case 'new_emergency_request':
            if (userRole === 'ambulance' && data?.id) {
              newNotification = {
                id: `emergency-${data.id}-${Date.now()}`,
                type: 'emergency',
                title: 'New Emergency Request',
                message: `${data?.patientCondition || 'Emergency'} - ${data?.address || 'Location not provided'}`,
                priority: data?.priority || 'high',
                timestamp: new Date(),
                read: false,
                actionable: true,
                data
              };
            }
            break;

          case 'ambulance_response':
            if (userRole === 'patient' && data?.patientId === userId && data?.id) {
              newNotification = {
                id: `ambulance-${data.id}-${Date.now()}`,
                type: 'ambulance',
                title: (data?.status === 'accepted') ? 'Ambulance Dispatched' : 'Request Update',
                message: (data?.status === 'accepted') 
                  ? 'Emergency services are on their way to your location'
                  : 'Your emergency request status has been updated',
                priority: (data?.status === 'accepted') ? 'high' : 'medium',
                timestamp: new Date(),
                read: false,
                actionable: false,
                data
              };
            }
            break;

          case 'emergency_status_update':
            if (data?.requestId) {
              newNotification = {
                id: `status-${data.requestId}-${Date.now()}`,
                type: 'emergency',
                title: 'Emergency Status Update',
                message: `Emergency request status: ${data?.status || 'updated'}`,
                priority: 'medium',
                timestamp: new Date(),
                read: false,
                actionable: false,
                data
              };
            }
            break;

          case 'new_message':
            if (data?.receiverId === userId && data?.receiverRole === userRole && data?.id) {
              newNotification = {
                id: `message-${data.id}-${Date.now()}`,
                type: 'communication',
                title: 'New Message',
                message: `${data?.senderRole || 'User'}: ${(data?.message || '').substring(0, 50)}...`,
                priority: 'medium',
                timestamp: new Date(),
                read: false,
                actionable: true,
                data
              };
            }
            break;

          case 'hospital_bed_update':
            if (userRole === 'ambulance' && data?.hospitalId) {
              newNotification = {
                id: `bed-${data.hospitalId}-${Date.now()}`,
                type: 'hospital',
                title: 'Hospital Bed Availability',
                message: `${data?.hospitalName || 'Hospital'}: ${data?.availableBeds || 0} beds available`,
                priority: 'low',
                timestamp: new Date(),
                read: false,
                actionable: false,
                data
              };
            }
            break;

          default:
            // Unknown message type, create generic notification
            if (data && event) {
              newNotification = {
                id: `generic-${Date.now()}`,
                type: 'communication',
                title: 'System Notification',
                message: `Received: ${event}`,
                priority: 'low',
                timestamp: new Date(),
                read: false,
                actionable: false,
                data
              };
            }
        }

        if (newNotification) {
          setNotifications(prev => [newNotification!, ...prev].slice(0, 20)); // Keep last 20 notifications

          // Show toast for critical/high priority notifications
          if (newNotification.priority === 'critical' || newNotification.priority === 'high') {
            toast({
              title: newNotification.title,
              description: newNotification.message,
              variant: newNotification.priority === 'critical' ? 'destructive' : 'default',
            });
          }
        }
      } catch (error) {
        console.warn('Notification processing error:', error);
      }
    }
  }, [lastMessage, userRole, userId, toast]);

  const markAsRead = (notificationId: string) => {
    setNotifications(prev => 
      prev.map(n => n.id === notificationId ? { ...n, read: true } : n)
    );
  };

  const dismissNotification = (notificationId: string) => {
    setNotifications(prev => prev.filter(n => n.id !== notificationId));
  };

  const clearAllNotifications = () => {
    setNotifications([]);
  };

  const unreadCount = notifications.filter(n => !n.read).length;

  const getPriorityIcon = (priority: string) => {
    switch (priority) {
      case 'critical':
        return <AlertTriangle className="w-4 h-4 text-red-500" />;
      case 'high':
        return <AlertTriangle className="w-4 h-4 text-orange-500" />;
      case 'medium':
        return <Clock className="w-4 h-4 text-yellow-500" />;
      default:
        return <CheckCircle className="w-4 h-4 text-green-500" />;
    }
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'emergency':
        return <AlertTriangle className="w-4 h-4" />;
      case 'communication':
        return <MessageSquare className="w-4 h-4" />;
      default:
        return <Bell className="w-4 h-4" />;
    }
  };

  return (
    <div className="relative">
      {/* Notification Bell */}
      <Button
        variant="outline"
        size="sm"
        onClick={() => setShowNotifications(!showNotifications)}
        className="relative"
      >
        <Bell className="w-4 h-4" />
        {unreadCount > 0 && (
          <Badge 
            variant="destructive" 
            className="absolute -top-2 -right-2 h-5 w-5 rounded-full p-0 flex items-center justify-center text-xs"
          >
            {unreadCount > 9 ? '9+' : unreadCount}
          </Badge>
        )}
      </Button>

      {/* Notification Panel */}
      {showNotifications && (
        <Card className="absolute right-0 top-12 w-80 max-h-96 overflow-y-auto z-50 shadow-lg">
          <CardContent className="p-0">
            <div className="flex items-center justify-between p-4 border-b">
              <h3 className="font-semibold">Notifications</h3>
              <div className="flex items-center space-x-2">
                {notifications.length > 0 && (
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={clearAllNotifications}
                    className="text-xs"
                  >
                    Clear All
                  </Button>
                )}
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setShowNotifications(false)}
                >
                  <X className="w-4 h-4" />
                </Button>
              </div>
            </div>

            {notifications.length === 0 ? (
              <div className="p-4 text-center text-gray-500">
                <Bell className="w-8 h-8 mx-auto mb-2 opacity-50" />
                <p>No notifications</p>
              </div>
            ) : (
              <div className="divide-y">
                {notifications.map((notification) => (
                  <div
                    key={notification.id}
                    className={`p-4 hover:bg-gray-50 cursor-pointer ${
                      !notification.read ? 'bg-blue-50 border-l-4 border-l-blue-500' : ''
                    }`}
                    onClick={() => markAsRead(notification.id)}
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex items-start space-x-3 flex-1">
                        <div className="flex items-center space-x-1">
                          {getTypeIcon(notification.type)}
                          {getPriorityIcon(notification.priority)}
                        </div>
                        <div className="flex-1 min-w-0">
                          <p className="font-medium text-sm truncate">
                            {notification.title}
                          </p>
                          <p className="text-xs text-gray-600 mt-1">
                            {notification.message}
                          </p>
                          <p className="text-xs text-gray-400 mt-1">
                            {notification.timestamp.toLocaleTimeString()}
                          </p>
                        </div>
                      </div>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={(e) => {
                          e.stopPropagation();
                          dismissNotification(notification.id);
                        }}
                        className="ml-2"
                      >
                        <X className="w-3 h-3" />
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      )}
    </div>
  );
}

