import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useAuth } from "@/hooks/use-auth";
import { useWebSocket } from "@/hooks/use-websocket";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { ScrollArea } from "@/components/ui/scroll-area";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { 
  Database, 
  Users, 
  Building2, 
  Ambulance, 
  AlertTriangle, 
  Bed, 
  MessageSquare,
  Plus,
  Trash2,
  Edit,
  RotateCcw,
  Download,
  Upload
} from "lucide-react";

interface TableData {
  columns: string[];
  rows: any[][];
  count: number;
}

const TABLE_CONFIGS = {
  users: { icon: Users, name: "Users", description: "User accounts and profiles" },
  hospitals: { icon: Building2, name: "Hospitals", description: "Hospital facilities and information" },
  ambulances: { icon: Ambulance, name: "Ambulances", description: "Ambulance fleet and operators" },
  emergency_requests: { icon: AlertTriangle, name: "Emergency Requests", description: "Emergency calls and dispatches" },
  bed_status_logs: { icon: Bed, name: "Bed Status", description: "Hospital bed availability logs" },
  communications: { icon: MessageSquare, name: "Communications", description: "System messages and chats" }
};

export default function AdminDashboard() {
  const { user } = useAuth();
  const { lastMessage } = useWebSocket();
  const [selectedTable, setSelectedTable] = useState<string>('users');
  const [sqlQuery, setSqlQuery] = useState<string>('');
  const [queryResult, setQueryResult] = useState<TableData | null>(null);
  const [isExecuting, setIsExecuting] = useState(false);

  // Check admin access
  if (!user || user.role !== 'admin') {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <Card className="w-96">
          <CardHeader>
            <CardTitle className="text-red-600">Access Denied</CardTitle>
            <CardDescription>You don't have permission to access the admin dashboard.</CardDescription>
          </CardHeader>
        </Card>
      </div>
    );
  }

  // Fetch table data
  const { data: tableData, isLoading, refetch } = useQuery({
    queryKey: [`/api/admin/table/${selectedTable}`],
    enabled: !!selectedTable
  });

  // Listen for real-time updates and refresh table data
  useEffect(() => {
    if (lastMessage && lastMessage.type === 'database_update') {
      refetch();
    }
  }, [lastMessage, refetch]);

  // Execute custom SQL query
  const sqlMutation = useMutation({
    mutationFn: async (query: string) => {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/admin/sql', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ query })
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`${response.status}: ${errorText}`);
      }

      return await response.json();
    },
    onSuccess: (data) => {
      setQueryResult(data);
    },
    onError: (error) => {
      console.error('SQL execution error:', error);
    }
  });

  // Delete record mutation
  const deleteMutation = useMutation({
    mutationFn: async ({ table, id }: { table: string; id: number }) => {
      const token = localStorage.getItem('token');
      const response = await fetch(`/api/admin/table/${table}/${id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`${response.status}: ${errorText}`);
      }

      return await response.json();
    },
    onSuccess: () => {
      refetch();
      queryClient.invalidateQueries({ queryKey: [`/api/admin/table/${selectedTable}`] });
    }
  });

  const handleExecuteSQL = () => {
    if (sqlQuery.trim()) {
      setIsExecuting(true);
      sqlMutation.mutate(sqlQuery);
      setIsExecuting(false);
    }
  };

  const handleDelete = (id: number) => {
    if (confirm('Are you sure you want to delete this record? This action cannot be undone.')) {
      deleteMutation.mutate({ table: selectedTable, id });
    }
  };

  const handleEdit = (rowIndex: number, columnIndex: number, newValue: string) => {
    // Implementation for inline editing
    console.log('Edit cell:', rowIndex, columnIndex, newValue);
  };

  const exportData = () => {
    if (tableData) {
      const csvContent = [
        tableData.columns.join(','),
        ...tableData.rows.map(row => row.map(cell => `"${cell || ''}"`).join(','))
      ].join('\n');

      const blob = new Blob([csvContent], { type: 'text/csv' });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `${selectedTable}_export.csv`;
      a.click();
      window.URL.revokeObjectURL(url);
    }
  };

  return (
    <div className="max-w-7xl mx-auto p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold flex items-center gap-3">
            <Database className="w-8 h-8 text-blue-600" />
            Admin Dashboard
          </h1>
          <p className="text-gray-600 mt-2">Complete system administration and database management</p>
        </div>
        <Badge variant="outline" className="px-3 py-1">
          {user.username} - Administrator
        </Badge>
      </div>

      <Tabs value={selectedTable} onValueChange={setSelectedTable} className="space-y-6">
        {/* Table Selection Tabs */}
        <div className="bg-white p-4 rounded-lg border">
          <TabsList className="grid grid-cols-6 w-full">
            {Object.entries(TABLE_CONFIGS).map(([key, config]) => {
              const IconComponent = config.icon;
              return (
                <TabsTrigger key={key} value={key} className="flex items-center gap-2">
                  <IconComponent className="w-4 h-4" />
                  <span className="hidden sm:inline">{config.name}</span>
                </TabsTrigger>
              );
            })}
          </TabsList>
        </div>

        {/* Table Content */}
        {Object.entries(TABLE_CONFIGS).map(([key, config]) => (
          <TabsContent key={key} value={key}>
            <Card>
              <CardHeader>
                <div className="flex items-center justify-between">
                  <div>
                    <CardTitle className="flex items-center gap-2">
                      <config.icon className="w-5 h-5" />
                      {config.name}
                    </CardTitle>
                    <CardDescription>{config.description}</CardDescription>
                  </div>
                  <div className="flex gap-2">
                    <Button variant="outline" size="sm" onClick={() => refetch()}>
                      <RotateCcw className="w-4 h-4 mr-2" />
                      Refresh
                    </Button>
                    <Button variant="outline" size="sm" onClick={exportData}>
                      <Download className="w-4 h-4 mr-2" />
                      Export CSV
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                {isLoading ? (
                  <div className="flex items-center justify-center py-8">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
                  </div>
                ) : tableData ? (
                  <div className="space-y-4">
                    <div className="text-sm text-gray-600">
                      Total records: {tableData.count}
                    </div>
                    <ScrollArea className="h-96 w-full border rounded">
                      <Table>
                        <TableHeader>
                          <TableRow>
                            {tableData.columns.map((column, index) => (
                              <TableHead key={index} className="font-semibold">
                                {column}
                              </TableHead>
                            ))}
                            <TableHead className="w-20">Actions</TableHead>
                          </TableRow>
                        </TableHeader>
                        <TableBody>
                          {tableData.rows.map((row, rowIndex) => (
                            <TableRow key={rowIndex}>
                              {row.map((cell, cellIndex) => (
                                <TableCell key={cellIndex} className="max-w-xs truncate">
                                  {cell !== null && cell !== undefined ? String(cell) : '-'}
                                </TableCell>
                              ))}
                              <TableCell>
                                <div className="flex gap-1">
                                  <Button 
                                    variant="ghost" 
                                    size="sm"
                                    onClick={() => handleEdit(rowIndex, 0, '')}
                                    className="text-blue-600 hover:text-blue-800 h-6 w-6 p-0"
                                  >
                                    <Edit className="w-3 h-3" />
                                  </Button>
                                  <Button 
                                    variant="ghost" 
                                    size="sm"
                                    onClick={() => handleDelete(row[0])}
                                    className="text-red-600 hover:text-red-800 h-6 w-6 p-0"
                                  >
                                    <Trash2 className="w-3 h-3" />
                                  </Button>
                                </div>
                              </TableCell>
                            </TableRow>
                          ))}
                        </TableBody>
                      </Table>
                    </ScrollArea>
                  </div>
                ) : (
                  <div className="text-center py-8 text-gray-500">
                    No data available
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>
        ))}
      </Tabs>

      {/* SQL Query Panel */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Database className="w-5 h-5" />
            Custom SQL Query
          </CardTitle>
          <CardDescription>
            Execute custom SQL queries directly on the database. Use with caution.
            <br />
            <span className="text-red-600 font-medium">‚ö†Ô∏è Important: Use single quotes (') for strings, not double quotes (")</span>
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="space-y-2">
            <div className="space-y-2">
              <Input
                placeholder="Enter SQL query (e.g., SELECT * FROM users WHERE role = 'patient')"
                value={sqlQuery}
                onChange={(e) => setSqlQuery(e.target.value)}
                className="font-mono"
                onKeyDown={(e) => {
                  if (e.key === 'Enter' && e.ctrlKey) {
                    handleExecuteSQL();
                  }
                }}
              />
              <div className="text-xs text-gray-600 bg-gray-50 p-2 rounded">
                <div className="font-semibold mb-1">Quick Examples:</div>
                <div className="space-y-1">
                  <div>‚Ä¢ <code>SELECT * FROM users WHERE role = 'patient'</code></div>
                  <div>‚Ä¢ <code>UPDATE users SET username = 'newname' WHERE id = 6</code></div>
                  <div>‚Ä¢ <code>INSERT INTO users (username, email) VALUES ('test', 'test@email.com')</code></div>
                  <div>‚Ä¢ <code>DELETE FROM emergency_requests WHERE status = 'completed'</code></div>
                </div>
              </div>
            </div>
            <div className="flex gap-2">
              <Button 
                onClick={handleExecuteSQL} 
                disabled={!sqlQuery.trim() || isExecuting}
                className="flex-1"
              >
                {isExecuting ? 'Executing...' : 'Execute Query (Ctrl+Enter)'}
              </Button>
              <Button 
                onClick={() => setSqlQuery("SELECT * FROM users WHERE role = 'patient' LIMIT 5")}
                variant="outline"
                size="sm"
              >
                Example
              </Button>
            </div>
          </div>

          {queryResult && (
            <div className="space-y-2">
              <h4 className="font-semibold">Query Result:</h4>
              <div className="text-sm text-gray-600">
                Returned {queryResult.rows.length} rows
              </div>
              <ScrollArea className="h-64 w-full border rounded">
                <Table>
                  <TableHeader>
                    <TableRow>
                      {queryResult.columns.map((column, index) => (
                        <TableHead key={index} className="font-semibold">
                          {column}
                        </TableHead>
                      ))}
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {queryResult.rows.map((row, rowIndex) => (
                      <TableRow key={rowIndex}>
                        {row.map((cell, cellIndex) => (
                          <TableCell key={cellIndex} className="max-w-xs truncate">
                            {cell !== null && cell !== undefined ? String(cell) : '-'}
                          </TableCell>
                        ))}
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </ScrollArea>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/ambulance-clean.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useWebSocket } from "@/hooks/use-websocket";
import { useGeolocation } from "@/hooks/use-geolocation";
import { useAuth } from "@/hooks/use-auth";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { 
  Ambulance, 
  AlertTriangle, 
  CheckCircle, 
  Clock, 
  MapPin, 
  MessageSquare,
  Navigation as NavigationIcon,
  User,
  Phone,
  X
} from "lucide-react";

export default function AmbulanceDashboard() {
  const { location } = useGeolocation();
  const { sendMessage } = useWebSocket();
  const [, setLocation] = useLocation();
  const [showRejectDialog, setShowRejectDialog] = useState<any>(null);
  const { user } = useAuth();

  const { data: emergencyRequests, isLoading: requestsLoading } = useQuery({
    queryKey: ['/api/emergency/requests'],
    // Removed refetchInterval - use real-time WebSocket updates only
  });

  // Update location periodically
  useEffect(() => {
    if (location) {
      sendMessage('ambulance:location_update', {
        lat: location.latitude,
        lng: location.longitude
      });
    }
  }, [location, sendMessage]);

  // Accept request mutation
  const acceptRequestMutation = useMutation({
    mutationFn: async ({ requestId, ambulanceId }: { requestId: number, ambulanceId: number }) => {
      const response = await apiRequest('PUT', `/api/emergency/request/${requestId}`, {
        status: 'dispatched',
        ambulanceId: ambulanceId
      });
      return response.json();
    },
    onSuccess: (data, variables) => {
      queryClient.invalidateQueries({ queryKey: ['/api/emergency/requests'] });
      // Navigate to map view with the accepted request
      setLocation(`/PatientTracking/${variables.requestId}`);
    },
    onError: (error) => {
      console.error('Failed to accept request:', error);
      alert('Failed to accept request. Please try again.');
    },
  });

  // Reject request mutation
  const rejectRequestMutation = useMutation({
    mutationFn: async ({ requestId }: { requestId: number }) => {
      const response = await apiRequest('PUT', `/api/emergency/request/${requestId}`, {
        status: 'cancelled'
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/emergency/requests'] });
      setShowRejectDialog(null);
    },
    onError: (error) => {
      console.error('Failed to reject request:', error);
      alert('Failed to reject request. Please try again.');
    },
  });

  const handleAcceptRequest = (request: any) => {
    // Get actual ambulance ID from user profile
    const ambulanceId = user?.ambulanceProfile?.id;
    if (!ambulanceId) {
      alert('Ambulance profile not found. Please contact support.');
      return;
    }

    acceptRequestMutation.mutate({
      requestId: request.id,
      ambulanceId: ambulanceId
    });
  };

  const handleRejectRequest = (request: any) => {
    setShowRejectDialog(request);
  };

  const confirmRejectRequest = () => {
    if (showRejectDialog) {
      rejectRequestMutation.mutate({
        requestId: showRejectDialog.id
      });
    }
  };

  const pendingRequests = Array.isArray(emergencyRequests) ? emergencyRequests.filter((req: any) => 
    req.status === 'pending'
  ) : [];

  return (
    <div className="max-w-6xl mx-auto p-4 space-y-6">
      {/* Reject Confirmation Dialog */}
      <Dialog open={!!showRejectDialog} onOpenChange={() => setShowRejectDialog(null)}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Reject Emergency Request</DialogTitle>
            <DialogDescription>
              Are you sure you want to reject this emergency request? This action cannot be undone.
            </DialogDescription>
          </DialogHeader>
          <div className="flex justify-end gap-4 mt-4">
            <Button variant="outline" onClick={() => setShowRejectDialog(null)}>
              Cancel
            </Button>
            <Button 
              variant="destructive" 
              onClick={confirmRejectRequest}
              disabled={rejectRequestMutation.isPending}
            >
              {rejectRequestMutation.isPending ? 'Rejecting...' : 'Reject Request'}
            </Button>
          </div>
        </DialogContent>
      </Dialog>

      {/* Status Header */}
      <Card>
        <CardContent className="p-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <div className="w-4 h-4 bg-green-500 rounded-full mr-3 animate-pulse"></div>
              <div>
                <div className="font-semibold text-gray-800">Ambulance Unit A-204</div>
                <div className="text-sm text-gray-600">Status: Available</div>
              </div>
            </div>
            <div className="flex items-center space-x-6">
              <div className="text-center">
                <div className="text-2xl font-bold text-blue-600">
                  {pendingRequests.length}
                </div>
                <div className="text-sm text-gray-600">Pending Requests</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-green-600">12</div>
                <div className="text-sm text-gray-600">Completed Today</div>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Emergency Requests */}
        <Card>
          <CardHeader>
            <CardTitle>Incoming Emergency Requests</CardTitle>
          </CardHeader>
          <CardContent>
            {requestsLoading ? (
              <div className="space-y-4">
                {[1, 2].map((i) => (
                  <div key={i} className="border rounded-lg p-4 animate-pulse">
                    <div className="flex items-center justify-between mb-3">
                      <div className="w-24 h-4 bg-gray-300 rounded"></div>
                      <div className="w-16 h-4 bg-gray-300 rounded"></div>
                    </div>
                    <div className="space-y-2">
                      <div className="w-32 h-3 bg-gray-300 rounded"></div>
                      <div className="w-24 h-3 bg-gray-300 rounded"></div>
                    </div>
                  </div>
                ))}
              </div>
            ) : pendingRequests.length > 0 ? (
              <div className="space-y-4">
                {pendingRequests.map((request: any) => (
                  <div key={request.id} className={`border-l-4 p-4 rounded-lg ${
                    request.priority === 'critical' ? 'border-red-500 bg-red-50' :
                    request.priority === 'high' ? 'border-orange-500 bg-orange-50' :
                    'border-yellow-500 bg-yellow-50'
                  }`}>
                    <div className="flex items-center justify-between mb-3">
                      <div className="flex items-center">
                        <AlertTriangle className={`w-4 h-4 mr-2 ${
                          request.priority === 'critical' ? 'text-red-500' :
                          request.priority === 'high' ? 'text-orange-500' :
                          'text-yellow-500'
                        }`} />
                        <span className={`font-semibold ${
                          request.priority === 'critical' ? 'text-red-700' :
                          request.priority === 'high' ? 'text-orange-700' :
                          'text-yellow-700'
                        }`}>
                          {request.patientCondition || 'Medical Emergency'}
                        </span>
                      </div>
                      <Badge variant="outline" className="text-xs">
                        {request.priority.toUpperCase()}
                      </Badge>
                    </div>

                    <div className="space-y-2 mb-4">
                      <div className="flex items-center text-sm text-gray-600">
                        <MapPin className="w-4 h-4 mr-2" />
                        {request.address}
                      </div>
                      <div className="flex items-center text-sm text-gray-600">
                        <Clock className="w-4 h-4 mr-2" />
                        {new Date(request.requestedAt).toLocaleTimeString()}
                      </div>
                      {request.notes && (
                        <div className="text-sm text-gray-600">
                          <strong>Notes:</strong> {request.notes}
                        </div>
                      )}
                    </div>

                    <div className="flex gap-2">
                      <Button 
                        onClick={() => handleAcceptRequest(request)}
                        disabled={acceptRequestMutation.isPending}
                        className="flex-1 bg-green-600 hover:bg-green-700"
                      >
                        <CheckCircle className="w-4 h-4 mr-2" />
                        {acceptRequestMutation.isPending ? 'Accepting...' : 'Accept'}
                      </Button>
                      <Button 
                        variant="destructive"
                        onClick={() => handleRejectRequest(request)}
                        disabled={rejectRequestMutation.isPending}
                        className="flex-1"
                      >
                        <X className="w-4 h-4 mr-2" />
                        Reject
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center py-8">
                <div className="mx-auto w-16 h-16 bg-gray-200 rounded-full flex items-center justify-center mb-4">
                  <Ambulance className="w-8 h-8 text-gray-400" />
                </div>
                <p className="text-gray-500">No pending emergency requests</p>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Quick Actions */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <NavigationIcon className="w-5 h-5 mr-2" />
              Quick Actions
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              <Button variant="outline" className="w-full justify-start">
                <MapPin className="w-4 h-4 mr-2" />
                Update Current Location
              </Button>
              <Button variant="outline" className="w-full justify-start">
                <Phone className="w-4 h-4 mr-2" />
                Emergency Contacts
              </Button>
              <Button variant="outline" className="w-full justify-start">
                <MessageSquare className="w-4 h-4 mr-2" />
                Communication Center
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/ambulance-navigation.ts.content:
import { useState, useEffect } from "react";
import { useParams } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { ArrowLeft, MapPin, Timer, Navigation as NavigationIcon, Clock, Heart, AlertTriangle, Phone, MessageSquare, CheckCircle } from "lucide-react";
import { useLocation } from "wouter";

export default function AmbulanceNavigation() {
  const { requestId } = useParams();
  const [, setLocation] = useLocation();
  const [eta, setEta] = useState("8 minutes");
  const [distance, setDistance] = useState("2.4 km");

  const { data: emergencyRequest, isLoading, isError } = useQuery({
    queryKey: ['/api/emergency/requests'],
    select: (data: any[]) => data?.find((req: any) => req.id === parseInt(requestId || '0')),
    enabled: !!requestId,
  });

  // Mock GPS coordinates for demonstration
  const mockCurrentLocation = { lat: 40.7580, lng: -73.9855 };
  const mockDestination = emergencyRequest ? {
    lat: parseFloat((emergencyRequest as any)?.latitude || '40.7128'),
    lng: parseFloat((emergencyRequest as any)?.longitude || '-74.0060')
  } : { lat: 40.7128, lng: -74.0060 };

  useEffect(() => {
    // Simulate ETA countdown
    const interval = setInterval(() => {
      setEta(prev => {
        const currentMinutes = parseInt(prev.split(' ')[0]);
        if (currentMinutes > 1) {
          return `${currentMinutes - 1} minutes`;
        }
        return "Arriving";
      });
    }, 10000); // Update every 10 seconds for demo

    return () => clearInterval(interval);
  }, []);

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'critical': return 'bg-red-500';
      case 'high': return 'bg-orange-500';
      case 'medium': return 'bg-yellow-500';
      case 'low': return 'bg-green-500';
      default: return 'bg-gray-500';
    }
  };

  if (isLoading) {
    return (
      <div className="max-w-4xl mx-auto p-4">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <h2 className="text-xl font-semibold">Loading emergency details...</h2>
        </div>
      </div>
    );
  }

  if (isError || !emergencyRequest) {
    return (
      <div className="max-w-4xl mx-auto p-4">
        <div className="text-center">
          <div className="text-red-500 mb-4">
            <AlertTriangle className="w-12 h-12 mx-auto mb-2" />
            <h2 className="text-xl font-semibold">Error Loading Emergency Request</h2>
            <p className="text-gray-600">Request ID: {requestId}</p>
          </div>
          <Button onClick={() => setLocation('/AmbulanceDashboard')}>
            Return to Dashboard
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto p-4 space-y-6">
      {/* Header */}
      <div className="flex items-center gap-4">
        <Button 
          variant="outline" 
          onClick={() => setLocation('/AmbulanceDashboard')}
          className="shrink-0"
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back to Dashboard
        </Button>
        <div>
          <h1 className="text-2xl font-bold">En Route to Emergency</h1>
          <p className="text-gray-600">Request ID: {emergencyRequest?.id}</p>
        </div>
      </div>

      {/* Emergency Details */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <AlertTriangle className="w-5 h-5 text-red-500" />
            Emergency Details
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <div className="flex items-center gap-2 mb-2">
                <Badge 
                  className={`${getPriorityColor(emergencyRequest?.priority || 'medium')} text-white`}
                >
                  {(emergencyRequest?.priority || 'medium').toUpperCase()} PRIORITY
                </Badge>
              </div>
              <h3 className="font-semibold mb-1">{emergencyRequest?.patientCondition || 'Medical Emergency'}</h3>
              <p className="text-gray-600 text-sm mb-2">{emergencyRequest?.notes || 'No additional notes'}</p>
              <div className="flex items-center gap-2 text-sm text-gray-500">
                <MapPin className="w-4 h-4" />
                {emergencyRequest?.address || 'Emergency Location'}
              </div>
            </div>
            <div className="space-y-2">
              <div className="flex items-center gap-2">
                <Clock className="w-4 h-4 text-gray-500" />
                <span className="text-sm">
                  Requested: {emergencyRequest?.requestedAt ? new Date(emergencyRequest.requestedAt).toLocaleTimeString() : 'Unknown'}
                </span>
              </div>
              <div className="flex items-center gap-2">
                <Heart className="w-4 h-4 text-red-500" />
                <span className="text-sm">Patient ID: {emergencyRequest?.patientId || 'Unknown'}</span>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Navigation Info */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Timer className="w-5 h-5 text-blue-500" />
              ETA & Distance
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-center space-y-4">
              <div>
                <div className="text-3xl font-bold text-blue-600">{eta}</div>
                <div className="text-gray-600">Estimated arrival</div>
              </div>
              <div>
                <div className="text-xl font-semibold">{distance}</div>
                <div className="text-gray-600">Distance remaining</div>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <NavigationIcon className="w-5 h-5 text-green-500" />
              Route Status
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              <div className="flex items-center justify-between">
                <span className="text-sm">Traffic Conditions</span>
                <Badge variant="outline" className="bg-green-50 text-green-700">
                  Light Traffic
                </Badge>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm">Route Status</span>
                <Badge variant="outline" className="bg-blue-50 text-blue-700">
                  Optimal Route
                </Badge>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm">GPS Signal</span>
                <Badge variant="outline" className="bg-green-50 text-green-700">
                  Strong
                </Badge>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Mock Map */}
      <Card>
        <CardHeader>
          <CardTitle>Navigation Map</CardTitle>
          <CardDescription>Real-time route to emergency location</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="w-full h-64 bg-gray-100 rounded-lg flex items-center justify-center relative">
            <div className="text-center">
              <MapPin className="w-12 h-12 text-blue-500 mx-auto mb-2" />
              <p className="text-gray-600 font-medium">Interactive Map View</p>
              <p className="text-sm text-gray-500">
                From: {mockCurrentLocation.lat.toFixed(4)}, {mockCurrentLocation.lng.toFixed(4)}
              </p>
              <p className="text-sm text-gray-500">
                To: {mockDestination.lat.toFixed(4)}, {mockDestination.lng.toFixed(4)}
              </p>
            </div>

            {/* Mock route indicators */}
            <div className="absolute top-4 left-4 bg-blue-500 w-3 h-3 rounded-full animate-pulse"></div>
            <div className="absolute bottom-4 right-4 bg-red-500 w-3 h-3 rounded-full"></div>
            <svg className="absolute inset-0 w-full h-full pointer-events-none">
              <path
                d="M 20 20 Q 150 100 250 240"
                stroke="#3B82F6"
                strokeWidth="3"
                fill="none"
                strokeDasharray="5,5"
                className="animate-pulse"
              />
            </svg>
          </div>
        </CardContent>
      </Card>

      {/* Quick Actions */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <Button className="h-12" variant="outline">
          <Phone className="w-4 h-4 mr-2" />
          Call Patient
        </Button>
        <Button className="h-12" variant="outline">
          <MessageSquare className="w-4 h-4 mr-2" />
          Contact Hospital
        </Button>
        <Button className="h-12 bg-green-600 hover:bg-green-700 text-white">
          <CheckCircle className="w-4 h-4 mr-2" />
          Mark Arrived
        </Button>
      </div>
    </div>
  );
}

